import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import './browseco.css';

const genres = [
  "Action", "Adventure", "Fantasy", "Mature", "Mystery", "Psychological",
  "Xianxia", "Wuxia", "Urban", "Xuanhuan", "Horror", "Harem", "Historical",
  "School Life", "Sci-fi", "Comedy", "Mecha"
];

const novels = [
  { title: "Reverend Insanity", genres: ["Fantasy", "Adventure", "Action","Xianxia", "Mystery"], cover: "/guzhenren-300w.jpg", popularity: 90, trending: 85, lastUpdated: '2022-01-10', url: "/novelpage"},
  { title: "I Shall Seal the Heavens", genres: ["Xianxia", "Mystery","Comedy", "Action","Adventure"], cover: "/woyufengtian-300w.jpg", popularity: 75, trending: 95, lastUpdated: '2024-03-15', url: "/novel2page"},
  { title: "A Record of a Mortal's Journey to Immortality", genres: ["Fantasy", "Xianxia","Action","Adventure","Mature"], cover: "/fanren-300w.jpg", popularity: 88, trending: 70, lastUpdated: '2023-12-01', url: "/novel3page"},
  { title: "Beyond the Timescape", genres: ["Fantasy", "Xianxia","Action","Adventure","Mature"], cover: "/guangy-300w.jpg", popularity: 87, trending: 80, lastUpdated: '2023-12-01', url: "/novel4page"},
  { title: "Epic of the Forsaken Hero", genres: ["Fantasy", "Adventure"], cover: "path/to/cover1.jpg", popularity: 90, trending: 85, lastUpdated: '2022-01-10', url: "/not-found"},
  { title: "Mysteries of the East", genres: ["Xianxia", "Mystery"], cover: "path/to/cover2.jpg", popularity: 75, trending: 95, lastUpdated: '2024-03-15' },
  { title: "School of Magic", genres: ["Fantasy", "School Life"], cover: "path/to/cover3.jpg", popularity: 88, trending: 70, lastUpdated: '2023-12-01' },
  { title: "Guardians of Lore", genres: ["Fantasy", "Epic"], cover: "path/to/cover4.jpg", popularity: 82, trending: 80, lastUpdated: '2022-12-25' },
  { title: "The Last Scholar", genres: ["Sci-fi", "Adventure"], cover: "path/to/cover5.jpg", popularity: 65, trending: 60, lastUpdated: '2024-01-20' },
  { title: "Battle for the Stars", genres: ["Sci-fi", "War"], cover: "path/to/cover6.jpg", popularity: 95, trending: 90, lastUpdated: '2023-11-11' },
  { title: "The Hidden Kingdom", genres: ["Mystery", "Fantasy"], cover: "path/to/cover7.jpg", popularity: 70, trending: 85, lastUpdated: '2023-07-07' },
  { title: "Ocean's Whispers", genres: ["Adventure", "Romance"], cover: "path/to/cover8.jpg", popularity: 80, trending: 75, lastUpdated: '2022-08-15' },
  { title: "Echoes of the Past", genres: ["Historical", "Mystery"], cover: "path/to/cover9.jpg", popularity: 78, trending: 82, lastUpdated: '2023-05-05' },
  { title: "Neon Dreams", genres: ["Urban", "Sci-fi"], cover: "path/to/cover10.jpg", popularity: 69, trending: 77, lastUpdated: '2022-09-09' }
];

const Browseco = ({ heading }) => {
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [sortMethod, setSortMethod] = useState('popular');
  const [filteredNovels, setFilteredNovels] = useState(novels);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  useEffect(() => {
    let results = novels.filter(novel => selectedGenres.every(genre => novel.genres.includes(genre)));
    switch (sortMethod) {
      case 'popular':
        results.sort((a, b) => b.popularity - a.popularity);
        break;
      case 'trending':
        results.sort((a, b) => b.trending - a.trending);
        break;
      case 'updated':
        results.sort((a, b) => new Date(b.lastUpdated) - new Date(a.lastUpdated));
        break;
    }
    setFilteredNovels(results);
  }, [selectedGenres, sortMethod]);

  return (
      <div className="browseco-container">
        <div className="sidebar">
          <button onClick={() => setIsSidebarOpen(!isSidebarOpen)} className="toggle-sidebar">
            Genres {isSidebarOpen ? '↑' : '↓'}
          </button>
          <div className={isSidebarOpen ? 'genres-list' : 'hidden'}>
            {genres.map(genre => (
                <button key={genre}
                        className={`genre-button ${selectedGenres.includes(genre) ? 'active' : ''}`}
                        onClick={() => setSelectedGenres(selectedGenres.includes(genre) ? selectedGenres.filter(g => g !== genre) : selectedGenres.length < 2 ? [...selectedGenres, genre] : selectedGenres)}>
                  {genre}
                </button>
            ))}
          </div>
        </div>
        <div className="content">
          <h2>{heading}</h2>
          <div className="sort-options">
            {['popular', 'trending', 'updated'].map(method => (
                <button key={method}
                        className={`sort-button ${sortMethod === method ? 'active-sort' : ''}`}
                        onClick={() => setSortMethod(method)}>
                  {method.charAt(0).toUpperCase() + method.slice(1)}
                </button>
            ))}
          </div>
          <div className="novel-list">
            {filteredNovels.map(novel => (
                <div key={novel.title} className="novel-item">
                  <a href={novel.url} className="novel-link">
                    <img src={novel.cover} alt={novel.title} className="novel-cover"/>
                    <h3 className="novel-title">{novel.title}</h3>
                  </a>
                </div>
            ))}
          </div>
        </div>
      </div>
  );
};

Browseco.propTypes = {
  heading: PropTypes.string,
};

Browseco.defaultProps = {
  heading: 'Order',
};

export default Browseco;
